
public class ArraysDioUm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Ordem Inversa
		
		int[] vetor = {-5, -6, 15, 50, 8, 4};
		
		for(int i = 0; i < vetor.length; i++){
			System.out.print(vetor[i] + " ");
		}
		
		System.out.println();
		
		for(int i = 5; i < 0; i--){
			System.out.print(vetor[i] + " ");
		}
		
	}

}
